function getSelected(t){return[...t.options].find(t=>t.selected)}function findMaxLengthOpt(t){return[...t.options].reduce((t,e)=>e.textContent.length>t?e.textContent.length:t,0)}let selectOrigin=document.querySelector(".selectOriDest"),selectDestOnly=document.querySelector(".selectDestOnly"),initialWidth=selectOrigin.offsetWidth,initialWidthO=selectDestOnly.offsetWidth,maxOptValLen=findMaxLengthOpt(selectOrigin),maxOptValLenO=findMaxLengthOpt(selectDestOnly),letterWidth=initialWidth/maxOptValLen,letterWidthO=initialWidthO/maxOptValLenO,corCoef=2,corCoefO=2;console.log(initialWidth,maxOptValLen),console.log(initialWidthO,maxOptValLenO),selectOrigin.addEventListener("change",t=>{let e=getSelected(t.target).textContent.length,n=(maxOptValLen-e)*corCoef,l=e*letterWidth+(n+24);console.log("new width",l,"new option len",e),t.target.style.width=l+"px"},!1),selectDestOnly.addEventListener("change",t=>{let e=getSelected(t.target).textContent.length,n=(maxOptValLen-e)*corCoef,l=e*letterWidth+(n+24);console.log("new width",l,"new option len",e),t.target.style.width=l+"px"},!1);